CREATE VIEW VW_EDAD2 (Nombres, Apellidos, Identificacion, FechaNacimiento, Email, Id_Grupo, NomGrupo, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.FechaNacimiento, T1.Email, T2.Id_Grupo, T2.NomGrupo, T4.Id_Proyecto, T4.NomProyecto FROM Contacto T1, Grupo T2, GrupoContacto T3, Proyecto T4 WHERE T1.Id_Contacto=T3.CodContacto AND T1.Id_Contacto=T4.CodContacto AND T2.Id_Grupo=T3.CodGrupo