CREATE VIEW VW_EDAD4 (Nombres, Apellidos, Identificacion, FechaNacimiento, Email, Id_Estado, NomEstado, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.FechaNacimiento, T1.Email, T2.Id_Estado, T2.NomEstado, T3.Id_Proyecto, T3.NomProyecto FROM Contacto T1, Estado T2, Proyecto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.Id_Estado=T3.CodEstado