CREATE VIEW VW_EJECUCION_1CONV_CIUDAD (razonsocial, ValorRecomendado, ValorEjecutado, NomSector) AS SELECT T4.razonsocial, T5.ValorRecomendado, Sum(T6.CantidadDinero), T8.NomSector FROM Ciudad T1, Convocatoria T2, ConvocatoriaProyecto T3, Empresa T4, EvaluacionObservacion T5, PagoActividad T6, Proyecto T7, Sector T8, SubSector T9 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T7.Id_Proyecto AND T4.codproyecto=T7.Id_Proyecto AND T5.CodProyecto=T7.Id_Proyecto AND T5.CodConvocatoria=1 AND T3.CodConvocatoria=1 AND T6.CodProyecto=T4.codproyecto AND T7.CodSubSector=T9.Id_SubSector AND T8.Id_Sector=T9.CodSector AND T2.encargofiduciario=103806 AND T1.Id_Ciudad=111 GROUP BY T4.razonsocial, T5.ValorRecomendado, T8.NomSector