CREATE VIEW VW_EJECUTAD_X_CIUDAD_DPTO (Id_Proyecto, NomProyecto, Id_Convocatoria, ValorRecomendado, AnnoConvocatoria, TotalRecomendado, DineroDesembolsado, NomCiudad, NomDepartamento) AS SELECT T1.Id_Proyecto, T1.NomProyecto, T1.Id_Convocatoria, T1.ValorRecomendado, T1.AnnoConvocatoria, T1.TotalRecomendado, T1.DineroDesembolsado, T2.NomCiudad, T3.NomDepartamento FROM VISTA_RECURSOS_PROYECTOS_EJECUCION T1, Ciudad T2, departamento T3, Proyecto T4 WHERE T2.CodDepartamento=T3.Id_Departamento AND T4.CodCiudad=T2.Id_Ciudad AND T4.Id_Proyecto=T1.Id_Proyecto