CREATE VIEW VW_EMPLEOS (Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, NomCiudad, NomDepartamento, Id_Proyecto, NomProyecto) AS SELECT T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T2.NomCiudad, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto FROM EmpleosGenerados T1, Ciudad T2, Convocatoria T3, departamento T4, Proyecto T5 WHERE T1.CodProyecto=T5.Id_Proyecto AND T2.CodDepartamento=T4.Id_Departamento AND T2.Id_Ciudad=T5.CodCiudad AND T5.CodEstado=3