CREATE VIEW VW_EMPLEOSAGENERARREGIONALES (CodProyecto, Total, Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, NomCiudad, NomDepartamento, Id_Proyecto, NomProyecto) AS SELECT T1.CodProyecto, T1.Total, T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T2.NomCiudad, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto FROM EmpleosGenerados T1, Ciudad T2, ConvocatoriaProyecto T3, departamento T4, Proyecto T5 WHERE T1.CodProyecto=T5.Id_Proyecto AND T3.CodProyecto=T5.Id_Proyecto AND T2.Id_Ciudad=T5.CodCiudad AND T2.CodDepartamento=T4.Id_Departamento AND T3.CodConvocatoria=13