CREATE VIEW VW_EMPLEOSGENERADOSPORCONVOCATORIA (Total, Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, NomCiudad, CodConvocatoria, Viable, NomDepartamento, Id_Proyecto, NomProyecto) AS SELECT T1.Total, T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T2.NomCiudad, T3.CodConvocatoria, T3.Viable, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto FROM EmpleosGenerados T1, Ciudad T2, ConvocatoriaProyecto T3, departamento T4, Proyecto T5 WHERE T1.CodProyecto=T5.Id_Proyecto AND T3.CodProyecto=T5.Id_Proyecto AND T2.CodDepartamento=T4.Id_Departamento AND T2.Id_Ciudad=T5.CodCiudad AND T3.CodConvocatoria=2