CREATE VIEW VW_EMPRENDEDORES_X_REGIONAL (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, CodConvocatoria, NomUnidad, Id_Proyecto, NomProyecto, CodRol) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T3.CodConvocatoria, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T6.CodRol FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, Institucion T4, Proyecto T5, ProyectoContacto T6 WHERE T3.CodProyecto=T5.Id_Proyecto AND T2.Id_Contacto=T5.CodContacto AND T4.Id_Institucion=T5.CodInstitucion AND T1.Id_Ciudad=T5.CodCiudad AND T2.Id_Contacto=T6.CodContacto AND T1.CodDepartamento=76 AND T3.Viable=1 AND T6.CodRol=3