CREATE VIEW VW_EMP_X_TIPO_CONV (Nombres, Apellidos, CodTipoIdentificacion, Identificacion, Email, CodConvocatoria, Id_Proyecto, NomProyecto, CodEstado, NomTipoAprendiz) AS SELECT T1.Nombres, T1.Apellidos, T1.CodTipoIdentificacion, T1.Identificacion, T1.Email, T2.CodConvocatoria, T3.Id_Proyecto, T3.NomProyecto, T3.CodEstado, T5.NomTipoAprendiz FROM Contacto T1, ConvocatoriaProyecto T2, Proyecto T3, ProyectoContacto T4, TipoAprendiz T5 WHERE T1.Id_Contacto=T4.CodContacto AND T1.CodTipoAprendiz=T5.Id_TipoAprendiz AND T4.CodProyecto=T2.CodProyecto AND T2.CodProyecto=T3.Id_Proyecto AND T3.CodEstado=4