CREATE VIEW VW_EVALUACIONNOVIABLESJUSTIFICACION (CodConvocatoria, Justificacion, ValorRecomendado, Id_Proyecto, NomProyecto, Recursos) AS SELECT T1.CodConvocatoria, T1.Justificacion, T2.ValorRecomendado, T3.Id_Proyecto, T3.NomProyecto, T4.Recursos FROM ConvocatoriaProyecto T1, EvaluacionObservacion T2, Proyecto T3, ProyectoFinanzasIngresos T4 WHERE T1.CodProyecto=T3.Id_Proyecto AND T2.CodProyecto=T3.Id_Proyecto AND T4.CodProyecto=T3.Id_Proyecto AND T1.CodConvocatoria=4 AND T2.CodConvocatoria=4 AND T3.CodEstado=4 AND T1.Viable=0