CREATE VIEW VW_EVALUACION_CONV_54 (CodConvocatoria, Justificacion, Viable, ValorRecomendado, Id_Proyecto, NomProyecto, CodEstado, Recursos) AS SELECT T1.CodConvocatoria, T1.Justificacion, T1.Viable, T2.ValorRecomendado, T3.Id_Proyecto, T3.NomProyecto, T3.CodEstado, T4.Recursos FROM ConvocatoriaProyecto T1, EvaluacionObservacion T2, Proyecto T3, ProyectoFinanzasIngresos T4 WHERE T1.CodProyecto=T2.CodProyecto AND T1.CodProyecto=T3.Id_Proyecto AND T1.CodProyecto=T4.CodProyecto AND T2.CodConvocatoria=53 AND T1.CodConvocatoria=53