CREATE VIEW VW_FC_UNIDADESEXTERNASACTIVAS (NomCiudad, NomDepartamento, NomInstitucion, NomUnidad, Inactivo, CodContacto, NomTipoInstitucion) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.NomInstitucion, T3.NomUnidad, T3.Inactivo, T4.CodContacto, T5.NomTipoInstitucion FROM Ciudad T1, departamento T2, Institucion T3, InstitucionContacto T4, TipoInstitucion T5 WHERE T2.Id_Departamento=T1.CodDepartamento AND T3.Id_Institucion=T4.CodInstitucion AND T5.Id_TipoInstitucion=T3.CodTipoInstitucion AND T1.Id_Ciudad=T3.CodCiudad AND T3.Inactivo=0