CREATE VIEW VW_FC_USUARIOS_PLAN (Nombres, Apellidos, Email, Clave, Id_Proyecto, NomProyecto, FechaInicio, FechaFin, Nombre) AS SELECT T1.Nombres, T1.Apellidos, T1.Email, T1.Clave, T2.Id_Proyecto, T2.NomProyecto, T3.FechaInicio, T3.FechaFin, T4.Nombre FROM Contacto T1, Proyecto T2, ProyectoContacto T3, Rol T4 WHERE T3.CodRol=T4.Id_Rol AND T2.Id_Proyecto=T3.CodProyecto AND T1.Id_Contacto=T3.CodContacto AND T3.CodProyecto=43307