CREATE VIEW VW_FORMACION_BENEFICIARIOS (CodContacto, TituloObtenido, AnoTitulo, Institucion, CodCiudad, Id_ContactoEstudio, FlagIngresadoAsesor, Finalizado, FechaGrado, SemestresCursados, FechaInicio, fechaCreacion, fechaActualizacion, FechaFinMaterias, CodConvocatoria, CodProyecto, NomInstitucionEducativa, NomNivelEstudio, NomProgramaAcademico) AS SELECT T1.CodContacto, T1.TituloObtenido, T1.AnoTitulo, T1.Institucion, T1.CodCiudad, T1.Id_ContactoEstudio, T1.FlagIngresadoAsesor, T1.Finalizado, T1.FechaGrado, T1.SemestresCursados, T1.FechaInicio, T1.fechaCreacion, T1.fechaActualizacion, T1.FechaFinMaterias, T2.CodConvocatoria, T2.CodProyecto, T3.NomInstitucionEducativa, T4.NomNivelEstudio, T5.NomProgramaAcademico FROM ContactoEstudio T1, ConvocatoriaProyecto T2, InstitucionEducativa T3, NivelEstudio T4, ProgramaAcademico T5, ProyectoContacto T6 WHERE T1.CodContacto=T6.CodContacto AND T1.CodNivelEstudio=T4.Id_NivelEstudio AND T1.CodProgramaAcademico=T5.Id_ProgramaAcademico AND T5.CodInstitucionEducativa=T3.Id_InstitucionEducativa AND T2.CodProyecto=T6.CodProyecto AND T2.Viable=1 AND T6.CodRol=3