CREATE VIEW VW_FORMACION_EMPRENDEDORES (NomCiudad, CodContacto, TituloObtenido, AnoTitulo, Institucion, Id_ContactoEstudio, FlagIngresadoAsesor, Finalizado, FechaGrado, FechaUltimoCorte, SemestresCursados, FechaInicio, fechaCreacion, fechaActualizacion, FechaFinMaterias, NomNivelEstudio, NomProgramaAcademico) AS SELECT T1.NomCiudad, T2.CodContacto, T2.TituloObtenido, T2.AnoTitulo, T2.Institucion, T2.Id_ContactoEstudio, T2.FlagIngresadoAsesor, T2.Finalizado, T2.FechaGrado, T2.FechaUltimoCorte, T2.SemestresCursados, T2.FechaInicio, T2.fechaCreacion, T2.fechaActualizacion, T2.FechaFinMaterias, T3.NomNivelEstudio, T4.NomProgramaAcademico FROM Ciudad T1, ContactoEstudio T2, NivelEstudio T3, ProgramaAcademico T4, ProyectoContacto T5 WHERE T2.CodContacto=T5.CodContacto AND T1.Id_Ciudad=T2.CodCiudad AND T2.CodNivelEstudio=T3.Id_NivelEstudio AND T4.Id_ProgramaAcademico=T2.CodProgramaAcademico AND T5.CodRol=3 AND T2.CodContacto<=20000