CREATE VIEW [dbo].[VW_GRUPOUSUARIO] (Count_T1_Id_Proyecto, Count_T1_CodConvocatoria, Nombres, Apellidos, Email, Clave) AS SELECT Count(T1.Id_Proyecto), Count(T1.CodConvocatoria), T2.Nombres, T2.Apellidos, T2.Email, T2.Clave FROM Conv_10_Avalados T1, Contacto T2, Grupo T3, GrupoContacto T4, Contacto T5, Grupo T6, GrupoContacto T7 WHERE T2.Identificacion=16075773 GROUP BY T2.Nombres, T2.Apellidos, T2.Email, T2.Clave