CREATE VIEW VW_GTEEVALUADOR_EVALUADOR2 (Nombres, Apellidos, N1, A1, Id_Proyecto, NomProyecto, NomTareaUsuario, Descripcion, Fecha, Respuesta, FechaCierre) AS SELECT T1.Nombres, T1.Apellidos, T3.Nombres, T3.Apellidos, T4.Id_Proyecto, T4.NomProyecto, T6.NomTareaUsuario, T6.Descripcion, T7.Fecha, T7.Respuesta, T7.FechaCierre FROM Contacto T1, ProyectoContacto T2, Contacto T3, Proyecto T4, ProyectoContacto T5, TareaUsuario T6, TareaUsuarioRepeticion T7 WHERE T6.CodContacto=T1.Id_Contacto AND T1.Id_Contacto=T2.CodContacto AND T2.CodProyecto=T4.Id_Proyecto AND T6.CodProyecto=T4.Id_Proyecto AND T6.CodContactoAgendo=T3.Id_Contacto AND T5.CodContacto=T3.Id_Contacto AND T2.CodProyecto=T5.CodProyecto AND T6.Id_TareaUsuario=T7.CodTareaUsuario AND T2.CodRol=3 AND T2.Inactivo=0 AND T5.CodRol=4 AND T4.CodEstado=4 AND T5.Inactivo=0