CREATE VIEW VW_GTEEVAL_EVALUADOR6 (Fecha, Respuesta, FechaCierre, Nombres, Apellidos, CodProyecto, NomTareaUsuario, Descripcion) AS SELECT T1.Fecha, T1.Respuesta, T1.FechaCierre, T3.Nombres, T3.Apellidos, T4.CodProyecto, T4.NomTareaUsuario, T4.Descripcion FROM TareaUsuarioRepeticion T1, GrupoContacto T2, Contacto T3, TareaUsuario T4 WHERE T2.CodGrupo=11 AND T4.CodContactoAgendo=6366 AND T3.Id_Contacto=T4.CodContacto AND T1.CodTareaUsuario=T4.Id_TareaUsuario AND T2.CodContacto=T3.Id_Contacto