CREATE VIEW VW_ICONOJEFESDEUNIDADXPROYECTO (NomCiudad, Nombres, Apellidos, Identificacion, Email, CodConvocatoria, NomDepartamento, NomUnidad, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, Max(T3.CodConvocatoria), T4.NomDepartamento, T5.NomUnidad, T7.Id_Proyecto, T7.NomProyecto FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Institucion T5, InstitucionContacto T6, Proyecto T7 WHERE T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Contacto=T6.CodContacto AND T3.CodProyecto=T7.Id_Proyecto AND T7.CodInstitucion=T5.Id_Institucion AND T5.CodCiudad=T1.Id_Ciudad AND T6.CodInstitucion=T5.Id_Institucion GROUP BY T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T4.NomDepartamento, T5.NomUnidad, T7.Id_Proyecto, T7.NomProyecto