CREATE VIEW VW_ICONOPAGOSBENEFICIARIOPORPROYECTO (CodConvocatoria, CantidadDinero, Estado, NomBanco, NumIdentificacion, Nombre, Apellido, RazonSocial, NumCuenta, Id_Proyecto, NomProyecto) AS SELECT Max(T1.CodConvocatoria), T3.CantidadDinero, T3.Estado, T4.NomBanco, T5.NumIdentificacion, T5.Nombre, T5.Apellido, T5.RazonSocial, T5.NumCuenta, T6.Id_Proyecto, T6.NomProyecto FROM ConvocatoriaProyecto T1, Empresa T2, PagoActividad T3, PagoBanco T4, PagoBeneficiario T5, Proyecto T6 WHERE T6.Id_Proyecto=T1.CodProyecto AND T6.Id_Proyecto=T2.codproyecto AND T6.Id_Proyecto=T3.CodProyecto AND T5.Id_PagoBeneficiario=T3.CodPagoBeneficiario AND T5.CodPagoBanco=T4.Id_Banco GROUP BY T3.CantidadDinero, T3.Estado, T4.NomBanco, T5.NumIdentificacion, T5.Nombre, T5.Apellido, T5.RazonSocial, T5.NumCuenta, T6.Id_Proyecto, T6.NomProyecto