CREATE VIEW VW_ICONOProyectoConvocatoria (CodConvocatoria, Viable, razonsocial, ObjetoSocial, CapitalSocial, NumEscrituraPublica, Telefono) AS SELECT T1.CodConvocatoria, T1.Viable, T2.razonsocial, T2.ObjetoSocial, T2.CapitalSocial, T2.NumEscrituraPublica, T2.Telefono FROM ConvocatoriaProyecto T1, Empresa T2, Proyecto T3 WHERE T3.Id_Proyecto=T2.codproyecto AND T3.Id_Proyecto=T1.CodProyecto AND T1.Viable=1 AND T1.CodConvocatoria=2