CREATE VIEW VW_ICONO_PAGOSPOREMPRESA (Convocatoria, razonsocial, No_Pagos, Valor_Pagado, Id_Proyecto, NomProyecto) AS SELECT Max(T1.CodConvocatoria), T2.razonsocial, Count(T3.Id_PagoActividad), Sum(T3.CantidadDinero), T4.Id_Proyecto, T4.NomProyecto FROM ConvocatoriaProyecto T1, Empresa T2, PagoActividad T3, Proyecto T4 WHERE T3.Estado=4 AND T2.codproyecto=T4.Id_Proyecto AND T4.Id_Proyecto=T1.CodProyecto AND T4.Id_Proyecto=T3.CodProyecto GROUP BY T2.razonsocial, T4.Id_Proyecto, T4.NomProyecto