CREATE VIEW VW_INFOCONTACTO (Nombres, Apellidos, Email, Telefono, Id_Proyecto, NomProyecto, Sumario, CodEstado, CodRol, Inactivo) AS SELECT T1.Nombres, T1.Apellidos, T1.Email, T1.Telefono, T2.Id_Proyecto, T2.NomProyecto, T2.Sumario, T2.CodEstado, T3.CodRol, T3.Inactivo FROM Contacto T1, Proyecto T2, ProyectoContacto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.Id_Proyecto=T3.CodProyecto