CREATE VIEW VW_INFORMACION_CONTACTO (Id_Contacto, Nombres, Apellidos, Identificacion, FechaNacimiento, Email, Direccion, Telefono, Inactivo, NomTipoAprendiz, NomTipoIdentificacion) AS SELECT T1.Id_Contacto, T1.Nombres, T1.Apellidos, T1.Identificacion, T1.FechaNacimiento, T1.Email, T1.Direccion, T1.Telefono, T1.Inactivo, T2.NomTipoAprendiz, T3.NomTipoIdentificacion FROM Contacto T1, TipoAprendiz T2, TipoIdentificacion T3 WHERE T3.Id_TipoIdentificacion=T1.CodTipoIdentificacion AND T2.Id_TipoAprendiz=T1.CodTipoAprendiz AND T1.Id_Contacto>88464