CREATE VIEW VW_INFORMECONTRAPARTIDAS (razonsocial, Id_Proyecto, NomProyecto, NomVariable, Cumplimiento, Observacion, Cumple, Seguimiento, IndicadorAsociado) AS SELECT T1.razonsocial, T3.Id_Proyecto, T3.NomProyecto, T4.NomVariable, T5.Cumplimiento, T5.Observacion, T5.Cumple, T5.Seguimiento, T5.IndicadorAsociado FROM Empresa T1, InformePresupuestal T2, Proyecto T3, Variable T4, VariableDetalle T5 WHERE T1.codproyecto=T3.Id_Proyecto AND T2.id_informepresupuestal=T5.CodInforme AND T2.codempresa=T1.id_empresa AND T4.id_Variable=T5.CodVariable AND T5.CodVariable=44