CREATE VIEW VW_INFORMESEGUIMIENTOINTERVENTORIAAMBITO (NomAmbito, Cumplimiento, Observacion, Cumple, Seguimiento, CodInforme, razonsocial) AS SELECT T1.NomAmbito, T2.Cumplimiento, T2.Observacion, T2.Cumple, T2.Seguimiento, T2.CodInforme, T3.razonsocial FROM Ambito T1, AmbitoDetalle T2, Empresa T3, InformePresupuestal T4 WHERE T1.id_ambito=T2.CodAmbito AND T2.CodInforme=T4.id_informepresupuestal AND T4.codempresa=T3.id_empresa