CREATE VIEW [dbo].[VW_INFORME_EVAL] (CodConvocatoria, Justificacion, Viable, ValorRecomendado, empleosevaluacion, Id_Proyecto, NomProyecto) AS SELECT T1.CodConvocatoria, T1.Justificacion, T1.Viable, T2.ValorRecomendado, T2.empleosevaluacion, T3.Id_Proyecto, T3.NomProyecto FROM ConvocatoriaProyecto T1, EvaluacionObservacion T2, Proyecto T3 WHERE T1.CodConvocatoria=T2.CodConvocatoria AND T2.CodProyecto=T3.Id_Proyecto AND T1.CodProyecto=T3.Id_Proyecto AND T1.CodConvocatoria=277