CREATE VIEW VW_INFORM_VISITA (NomCiudad, Nombres, Apellidos, NomDepartamento, razonsocial, NombreInforme, FechaSalida, FechaRegreso, InformacionTecnica, FechaInforme, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T3.NomDepartamento, T4.razonsocial, T6.NombreInforme, T6.FechaSalida, T6.FechaRegreso, T6.InformacionTecnica, T6.FechaInforme, T7.Id_Proyecto, T7.NomProyecto FROM Ciudad T1, Contacto T2, departamento T3, Empresa T4, EmpresaInterventor T5, InformeVisitaInterventoria T6, Proyecto T7 WHERE T2.Id_Contacto=T5.CodContacto AND T3.Id_Departamento=T1.CodDepartamento AND T4.id_empresa=T6.CodEmpresa AND T4.codproyecto=T7.Id_Proyecto AND T5.Inactivo=0 AND T1.Id_Ciudad=T4.CodCiudad