CREATE VIEW VW_INF_VISITA_C_ORIGEN (NomCiudad, Nombres, Apellidos, razonsocial, NombreInforme, FechaSalida, FechaRegreso, CostoVisita, InformacionTecnica, InformacionFinanciera, FechaInforme) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T3.razonsocial, T5.NombreInforme, T5.FechaSalida, T5.FechaRegreso, T5.CostoVisita, T5.InformacionTecnica, T5.InformacionFinanciera, T5.FechaInforme FROM Ciudad T1, Contacto T2, Empresa T3, EmpresaInterventor T4, InformeVisitaInterventoria T5 WHERE T1.Id_Ciudad=T5.CodCiudadOrigen AND T2.Id_Contacto=T4.CodContacto AND T3.id_empresa=T4.CodEmpresa AND T5.CodEmpresa=T3.id_empresa AND T5.CodInterventor=T4.CodContacto