CREATE VIEW VW_INSTITUCION_EXTERNA (NomCiudad, Nombres, Apellidos, Identificacion, Email, NomDepartamento, NomInstitucion, NomUnidad, Direccion, Telefono, FechaInicio, FechaFin, NomTipoInstitucion) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T4.Direccion, T4.Telefono, T5.FechaInicio, T5.FechaFin, T6.NomTipoInstitucion FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, InstitucionContacto T5, TipoInstitucion T6 WHERE T4.Id_Institucion=T5.CodInstitucion AND T6.Id_TipoInstitucion=T4.CodTipoInstitucion AND T2.Id_Contacto=T5.CodContacto AND T1.Id_Ciudad=T4.CodCiudad AND T3.Id_Departamento=T1.CodDepartamento AND T6.Id_TipoInstitucion=3