CREATE VIEW VW_INTERVENTORXEMPRESA (NomCiudad, Nombres, Apellidos, Email, Direccion, Telefono, razonsocial, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Direccion, T2.Telefono, T3.razonsocial, T5.Id_Proyecto, T5.NomProyecto FROM Ciudad T1, Contacto T2, Empresa T3, EmpresaInterventor T4, Proyecto T5 WHERE T1.Id_Ciudad=T5.CodCiudad AND T2.Id_Contacto=T4.CodContacto AND T4.CodEmpresa=T3.id_empresa AND T5.Id_Proyecto=T3.codproyecto AND T4.Inactivo=0