CREATE VIEW VW_INTERVENTOR_EMPLEOS (Nombres, Apellidos, razonsocial, codproyecto, Inactivo, NomInformeBimensual, Estado, Periodo, Fecha, NomVariable, CodVariable, Observacion) AS SELECT T1.Nombres, T1.Apellidos, T2.razonsocial, T2.codproyecto, T3.Inactivo, T4.NomInformeBimensual, T4.Estado, T4.Periodo, T4.Fecha, T5.NomVariable, T6.CodVariable, T6.Observacion FROM Contacto T1, Empresa T2, EmpresaInterventor T3, InformeBimensual T4, Variable T5, VariableDetalle T6 WHERE T1.Id_Contacto=T3.CodContacto AND T2.id_empresa=T4.codempresa AND T2.id_empresa=T3.CodEmpresa AND T4.codempresa=T2.id_empresa AND T5.CodTipoVariable=T6.CodVariable AND T6.CodInforme=T4.id_informeBimensual AND T3.Inactivo=0