CREATE VIEW VW_JEFESUNIDAD (NomCiudad, Nombres, Apellidos, CodTipoIdentificacion, Identificacion, Genero, FechaNacimiento, Cargo, Email, NomDepartamento, NomInstitucion, Direccion, Telefono, FechaInicio, FechaFin) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.CodTipoIdentificacion, T2.Identificacion, T2.Genero, T2.FechaNacimiento, T2.Cargo, T2.Email, T3.NomDepartamento, T4.NomInstitucion, T4.Direccion, T4.Telefono, T5.FechaInicio, T5.FechaFin FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, InstitucionContacto T5 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T2.CodInstitucion=T4.Id_Institucion AND T2.Id_Contacto=T5.CodContacto