CREATE VIEW VW_JEFES_UNIDAD_HISTORICO (NomInstitucion, NomUnidad, Id_Contacto, Nombres, Apellidos, CodTipoIdentificacion, Identificacion, Genero, FechaNacimiento, Cargo, Email, Direccion, Telefono, Fax, Inactivo, CodContacto, FechaInicio, FechaFin, MotivoCambioJefe) AS SELECT T1.NomInstitucion, T1.NomUnidad, T2.Id_Contacto, T2.Nombres, T2.Apellidos, T2.CodTipoIdentificacion, T2.Identificacion, T2.Genero, T2.FechaNacimiento, T2.Cargo, T2.Email, T2.Direccion, T2.Telefono, T2.Fax, T2.Inactivo, T4.CodContacto, T4.FechaInicio, T4.FechaFin, T4.MotivoCambioJefe FROM VISTA_JEFES_UNIDAD_ACTIVOS T1, Contacto T2, Institucion T3, InstitucionContacto T4 WHERE T2.Id_Contacto=T4.CodContacto AND T3.Id_Institucion=T4.CodInstitucion AND T3.Id_Institucion=59