CREATE VIEW VW_JefesUNIdad1 (NomCiudad, Nombres, Apellidos, Email, Direccion, Telefono, NomDepartamento, NomInstitucion, NomUnidad, Dir, Tel, FechaFin, MotivoCambioJefe) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Direccion, T2.Telefono, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T4.Direccion, T4.Telefono, T5.FechaFin, T5.MotivoCambioJefe FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, InstitucionContacto T5 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T4.Id_Institucion=T5.CodInstitucion AND T5.CodContacto=T2.Id_Contacto