CREATE VIEW VW_NIT_PLANES_EN_EJECUCION (Nit, Id_Proyecto) AS SELECT T4.Nit, T6.Id_Proyecto FROM Ciudad T1, Contacto T2, departamento T3, Empresa T4, EmpresaContacto T5, Proyecto T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T4.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.Id_Contacto=T5.codcontacto AND T4.codproyecto=T6.Id_Proyecto AND T4.id_empresa=T5.codempresa AND T6.CodSubSector=T8.Id_SubSector AND T8.CodSector=T7.Id_Sector AND T5.RepresentanteLegal=1 AND T6.CodEstado=7