CREATE VIEW VW_NIVEL_DE_ESTUDIO_CONV (Nombres, Apellidos, TituloObtenido, CodConvocatoria, NomNivelEstudio, CodProyecto, CodRol) AS SELECT T1.Nombres, T1.Apellidos, T2.TituloObtenido, T3.CodConvocatoria, T4.NomNivelEstudio, T5.CodProyecto, T5.CodRol FROM Contacto T1, ContactoEstudio T2, ConvocatoriaProyecto T3, NivelEstudio T4, ProyectoContacto T5 WHERE T1.Id_Contacto=T2.CodContacto AND T1.Id_Contacto=T5.CodContacto AND T3.CodProyecto=T5.CodProyecto AND T4.Id_NivelEstudio=T2.CodNivelEstudio AND T3.CodConvocatoria=69