CREATE VIEW VW_P (CodConvocatoria, CodProyecto, Id_Riesgo, proyecto, Id_TipoInstitucion, NomTipoInstitucion) AS SELECT T1.CodConvocatoria, T1.CodProyecto, T2.Id_Riesgo, T2.CodProyecto, T3.Id_TipoInstitucion, T3.NomTipoInstitucion FROM ConvocatoriaProyecto T1, EvaluacionRiesgo T2, TipoInstitucion T3 WHERE T1.CodConvocatoria=T2.Id_Riesgo AND T1.CodConvocatoria=T3.Id_TipoInstitucion