CREATE VIEW VW_PAGOACTIVIDADACTA (Id_PagoActividad, FechaIngreso, FechaInterventor, FechaCoordinador, FechaRtaFA, CodPagosActaSolicitudes, Id_Proyecto, NomProyecto) AS SELECT T1.Id_PagoActividad, T1.FechaIngreso, T1.FechaInterventor, T1.FechaCoordinador, T1.FechaRtaFA, T3.CodPagosActaSolicitudes, T4.Id_Proyecto, T4.NomProyecto FROM PagoActividad T1, PagoBeneficiario T2, PagosActaSolicitudPagos T3, Proyecto T4 WHERE T1.Id_PagoActividad=T3.CodPagoActividad AND T1.CodPagoBeneficiario=T2.Id_PagoBeneficiario AND T1.CodProyecto=T4.Id_Proyecto AND T1.Estado=3