CREATE VIEW VW_PAGOSAPROBADOSCAPACITACION (Id_PagoActividad, NomPagoActividad, Mes, Observaciones, CantidadDinero, CodProyecto, Estado, FechaIngreso, FechaInterventor, FechaCoordinador, FechaRtaFA, valorretefuente, valorreteiva, valorreteica, otrosdescuentos, valorpagado, fechapago, RazonSocial) AS SELECT T1.Id_PagoActividad, T1.NomPagoActividad, T1.Mes, T1.Observaciones, T1.CantidadDinero, T1.CodProyecto, T1.Estado, T1.FechaIngreso, T1.FechaInterventor, T1.FechaCoordinador, T1.FechaRtaFA, T1.valorretefuente, T1.valorreteiva, T1.valorreteica, T1.otrosdescuentos, T1.valorpagado, T1.fechapago, T2.RazonSocial FROM PagoActividad T1, PagoBeneficiario T2 WHERE T1.CodPagoBeneficiario=T2.Id_PagoBeneficiario AND T1.Estado=4