CREATE VIEW VW_PAGOSXCONVOCATORA (NomConvocatoria, razonsocial, ValorRecomendado, Count_T5_Id_PagoActividad, Sum_T5_CantidadDinero, Id_Proyecto, NomProyecto) AS SELECT T1.NomConvocatoria, T3.razonsocial, T4.ValorRecomendado, Count(T5.Id_PagoActividad), Sum(T5.CantidadDinero), T6.Id_Proyecto, T6.NomProyecto FROM Convocatoria T1, ConvocatoriaProyecto T2, Empresa T3, EvaluacionObservacion T4, PagoActividad T5, Proyecto T6 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T6.Id_Proyecto AND T3.codproyecto=T6.Id_Proyecto AND T3.codproyecto=T5.CodProyecto AND T4.CodProyecto=T6.Id_Proyecto AND T5.Estado=4 AND T1.encargofiduciario=90594 GROUP BY T1.NomConvocatoria, T3.razonsocial, T4.ValorRecomendado, T6.Id_Proyecto, T6.NomProyecto