CREATE VIEW VW_PAGOS_1_CONVOCATORIA_NEW (razonsocial, Numero_desembolsos, Valor_desembolsado, Id_Proyecto, NomProyecto) AS SELECT T3.razonsocial, Count(T5.Id_PagoActividad), Sum(T5.CantidadDinero), T6.Id_Proyecto, T6.NomProyecto FROM Convocatoria T1, ConvocatoriaProyecto T2, Empresa T3, LegalizacionActaProyecto T4, PagoActividad T5, Proyecto T6 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T6.Id_Proyecto=T2.CodProyecto AND T6.Id_Proyecto=T3.codproyecto AND T6.Id_Proyecto=T5.CodProyecto AND T6.Id_Proyecto=T4.CodProyecto AND T4.Legalizado=1 AND T4.CodActa>=1 AND T4.CodActa<=58 AND T5.Estado=4 GROUP BY T3.razonsocial, T6.Id_Proyecto, T6.NomProyecto