CREATE VIEW VW_PAGOS_2_CONVOCATORIA (Id_Convocatoria, NomConvocatoria, razonsocial, Sum_T5_CantidadDinero, Estado, FechaIngreso, FechaInterventor, FechaCoordinador, codigopago, valorretefuente, valorreteiva, valorreteica, valorpagado, Id_Proyecto, NomProyecto) AS SELECT T1.Id_Convocatoria, T1.NomConvocatoria, T3.razonsocial, Sum(T5.CantidadDinero), T5.Estado, T5.FechaIngreso, T5.FechaInterventor, T5.FechaCoordinador, T5.codigopago, T5.valorretefuente, T5.valorreteiva, T5.valorreteica, T5.valorpagado, T6.Id_Proyecto, T6.NomProyecto FROM Convocatoria T1, ConvocatoriaProyecto T2, Empresa T3, EvaluacionObservacion T4, PagoActividad T5, Proyecto T6 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T6.Id_Proyecto AND T3.codproyecto=T6.Id_Proyecto AND T3.codproyecto=T5.CodProyecto AND T4.CodProyecto=T6.Id_Proyecto AND T4.CodConvocatoria=42 GROUP BY T1.Id_Convocatoria, T1.NomConvocatoria, T3.razonsocial, T5.Estado, T5.FechaIngreso, T5.FechaInterventor, T5.FechaCoordinador, T5.codigopago, T5.valorretefuente, T5.valorreteiva, T5.valorreteica, T5.valorpagado, T6.Id_Proyecto, T6.NomProyecto