CREATE VIEW VW_PAGOS_COORDINACION (razonsocial, Id_PagoActividad, CantidadDinero, Estado, FechaIngreso, FechaInterventor, FechaCoordinador, ObservaInterventor, Id_Proyecto, NomProyecto) AS SELECT T1.razonsocial, T2.Id_PagoActividad, T2.CantidadDinero, T2.Estado, T2.FechaIngreso, T2.FechaInterventor, T2.FechaCoordinador, T2.ObservaInterventor, T3.Id_Proyecto, T3.NomProyecto FROM Empresa T1, PagoActividad T2, Proyecto T3 WHERE T1.codproyecto=T2.CodProyecto AND T1.codproyecto=T3.Id_Proyecto AND T2.Estado=3