CREATE VIEW VW_PESCAC1YC2 (NomCiudad, encargofiduciario, NomDepartamento, razonsocial, Id_Proyecto, NomProyecto, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.encargofiduciario, T4.NomDepartamento, T5.razonsocial, T6.Id_Proyecto, T6.NomProyecto, T7.NomSector, T8.NomSubSector FROM Ciudad T1, Convocatoria T2, ConvocatoriaProyecto T3, departamento T4, Empresa T5, Proyecto T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T6.CodCiudad AND T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T6.Id_Proyecto AND T5.codproyecto=T6.Id_Proyecto AND T6.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector AND T7.Id_Sector=2