CREATE VIEW VW_PLANES_PRESENTADOS_FECHAAVAL (NomCiudad, NomDepartamento, Id_Proyecto, NomProyecto, FechaCreacion, Recursos, Fecha, Aval, Observaciones, CodConvocatoria) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.Id_Proyecto, T3.NomProyecto, T3.FechaCreacion, T4.Recursos, T5.Fecha, T5.Aval, T5.Observaciones, T5.CodConvocatoria FROM Ciudad T1, departamento T2, Proyecto T3, ProyectoFinanzasIngresos T4, ProyectoFormalizacion T5 WHERE T1.CodDepartamento=T2.Id_Departamento AND T1.Id_Ciudad=T3.CodCiudad AND T3.Id_Proyecto=T4.CodProyecto AND T3.Id_Proyecto=T5.codProyecto