CREATE VIEW VW_PLANES_REGISTRADOS (NomCiudad, CodConvocatoria, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, Fecha, Aval) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T6.Recursos, T7.Fecha, T7.Aval FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Institucion T4, Proyecto T5, ProyectoFinanzasIngresos T6, ProyectoFormalizacion T7 WHERE T1.CodDepartamento=T3.Id_Departamento AND T2.CodProyecto=T5.Id_Proyecto AND T5.CodInstitucion=T4.Id_Institucion AND T5.Id_Proyecto=T6.CodProyecto AND T5.Id_Proyecto=T7.codProyecto AND T1.Id_Ciudad=T5.CodCiudad