CREATE VIEW VW_PPROYECCION_VENTAS100206 (Periodo, Precio, encargofiduciario, razonsocial, Id_Proyecto, NomProyecto, PrecioLanzamiento, UNIDADES) AS SELECT T1.Periodo, T1.Precio, T2.encargofiduciario, T4.razonsocial, T5.Id_Proyecto, T5.NomProyecto, T6.PrecioLanzamiento, Sum(T7.Unidades) FROM V_ProyectoProductoPrecio T1, Convocatoria T2, ConvocatoriaProyecto T3, Empresa T4, Proyecto T5, ProyectoProducto T6, ProyectoProductoUnidadesVentas T7 WHERE T1.CodProducto=T6.Id_Producto AND T1.CodProducto=T7.CodProducto AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T5.Id_Proyecto AND T4.codproyecto=T5.Id_Proyecto AND T6.CodProyecto=T5.Id_Proyecto AND T1.Periodo=T7.Ano AND T7.Ano=1 GROUP BY T1.Periodo, T1.Precio, T2.encargofiduciario, T4.razonsocial, T5.Id_Proyecto, T5.NomProyecto, T6.PrecioLanzamiento