CREATE VIEW VW_PRODUCCION_CANTIDAD_C1 (Cantidad_Productos, Producido, razonsocial, Id_Proyecto, NomProyecto) AS SELECT Count(T1.CodProducto), Sum(T1.Valor), T4.razonsocial, T6.Id_Proyecto, T6.NomProyecto FROM AvanceProduccionPOMes T1, Convocatoria T2, ConvocatoriaProyecto T3, Empresa T4, InterventorProduccion T5, Proyecto T6 WHERE T1.CodProducto=T5.id_produccion AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T6.Id_Proyecto AND T4.codproyecto=T6.Id_Proyecto AND T5.CodProyecto=T6.Id_Proyecto AND T2.encargofiduciario=103806 AND T1.CodTipoFinanciacion=1 AND T3.CodConvocatoria=1 GROUP BY T4.razonsocial, T6.Id_Proyecto, T6.NomProyecto