CREATE VIEW VW_PROYECCION_VENTAS (Id_Producto, NomProducto, Periodo, Precio, unidades) AS SELECT T1.Id_Producto, T1.NomProducto, T2.Periodo, T2.Precio, Sum(T3.Unidades) FROM ProyectoProducto T1, ProyectoProductoPrecio T2, ProyectoProductoUnidadesVentas T3 WHERE T1.Id_Producto=T2.CodProducto AND T1.Id_Producto=T3.CodProducto AND T2.Periodo=T3.Ano AND T1.CodProyecto=2885 GROUP BY T1.Id_Producto, T1.NomProducto, T2.Periodo, T2.Precio